import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

const certificates = [
  {
    title: "Java (Basic)",
    field: "HackerRank",
    skills: "Java, Problem Solving",
    credential: "https://www.hackerrank.com/certificates/abc123",
    date: "May 2024"
  },
  {
    title: "Python for Data Science",
    field: "NPTEL",
    skills: "Python, Data Analysis",
    credential: "https://nptel.ac.in/certificates/xyz456",
    date: "Aug 2023"
  },
  {
    title: "Oracle Cloud Infrastructure",
    field: "Oracle",
    skills: "Cloud Computing, OCI",
    credential: "https://oracle.com/certificates/oci789",
    date: "Jan 2024"
  }
];

const Certificates = () => {
  return (
    <section style={{ backgroundColor: "#000", color: "#FFD700", minHeight: "100vh", padding: "50px 0" }}>
      <Container>
        <h2 className="text-center fw-bold mb-5">My Certificates</h2>
        <Row xs={1} md={2} lg={3} className="g-4">
          {certificates.map((cert, index) => (
            <Col key={index}>
              <Card className="h-100 text-light border border-warning shadow hover-shadow bg-dark">
                <Card.Body>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <Card.Title className="fw-bold mb-0">{cert.title}</Card.Title>
                    <small className="text-muted" style={{ fontSize: "0.9rem" }}>{cert.date}</small>
                  </div>
                  <Card.Subtitle className="mb-2 text-warning">{cert.field}</Card.Subtitle>
                  <Card.Text><strong>Skills:</strong> {cert.skills}</Card.Text>
                  <Button
                    href={cert.credential}
                    target="_blank"
                    rel="noopener noreferrer"
                    variant="outline-warning"
                    className="mt-2"
                  >
                    View Credential
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default Certificates;
